package com.example.razorpayimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Buttonimport android.widget.EditTextimport androidx.appcompat.app.AlertDialogimport androidx.recyclerview.widget.RecyclerViewimport com.example.razorpay.Modal.GetItemimport com.example.razorpay.Modal.MyDataimport com.example.razorpay.Modal.Notesimport com.example.razorpay.Modal.PutModalimport com.example.razorpay.databinding.GetLayoutBindingimport com.google.gson.Gsonimport okhttp3.Credentialsimport okhttp3.MediaType.Companion.toMediaTypeOrNullimport okhttp3.OkHttpClientimport okhttp3.Requestimport okhttp3.RequestBody.Companion.toRequestBodyclass USerAdapter(    private val context: Context,    private val userList: List<GetItem>,    private val clickListener: ClickListener) : RecyclerView.Adapter<USerAdapter.ViewHolder>() {    class ViewHolder(view: View) : RecyclerView.ViewHolder(view) {        val binding = GetLayoutBinding.bind(view)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {        return ViewHolder(LayoutInflater.from(context).inflate(R.layout.get_layout, parent, false))    }    override fun getItemCount(): Int = userList.size    override fun onBindViewHolder(holder: ViewHolder, position: Int) {        val list = userList[position]        holder.binding.emailGEt.text = list.email        holder.binding.numberGEt.text = list.contact        holder.binding.nameGEt.text = list.name        holder.binding.root.setOnLongClickListener {            clickListener.putData(position)            true        }    }}interface ClickListener {    fun putData(position: Int)}